package d0321;

public class PPExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int A = 5;
		
		System.out.println(A++);
		System.out.println(A);
		
		System.out.println("---------------");
		
		int B = 5;
		
		System.out.println(++B);
		System.out.println(B);
	}
}
