package q599;

import java.util.Scanner;

public class Main {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int a = sc.nextInt();
		if(a>=65 && a<=90 ) {
			System.out.println(a);
		}else if (a>=97 && a<=122) { 
			System.out.println(a>=65 && a<=90);
		}
	}
}
